import React from "react";
import axios from "axios";
import {Taskcard} from "../cards/viewtaskcard"

class View_tasks extends React.Component{
    constructor(props){
     super(props);
     this.state={
         userId:this.props.userdata.data.data.userDetails.userId,
         listId:this.props.data.listId,
         authToken:this.props.userdata.data.data.authToken,
         taskcontent:"",
         alltasks:undefined
        }
     this.handlesubmit=this.handlesubmit.bind(this);
     this.changehandler=this.changehandler.bind(this);
    }

    componentDidMount(){
       
        axios
        .get('http://127.0.0.1:3000/api/v1/task/view/'+this.state.userId+'/'+this.state.listId+'/'+this.state.authToken)
        .then(response =>{
            console.log("this is views response")
            console.log(response)
            console.log("above is views response")
           const fulldata= (response.data).map((td) =><Taskcard key={td.taskId} data={td} /> )
           this.setState({alltasks:fulldata})
        })
        .catch(error =>{
            alert("Error")
            console.log(error)
        })
   
    }
     
    handlesubmit(e){
        e.preventDefault();
        alert("in handle submit")
        axios.post('http://127.0.0.1:3000/api/v1/tasks/create/'+this.state.userId+'/'+this.state.listId+'/'+this.state.authToken)
        .then(response =>{ 
          
            console.log(response)
            alert("task posted")
            
        })
        .catch(error=>{
            alert("error")
           console.log(error)
        })


    }
    changehandler(e){
     this.setState({[e.target.name]:e.target.value})

    }
    render(){
        return(
            <div>

<html>
    <head>
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossorigin="anonymous" />
    </head>
    <body >
            <form onSubmit={this.handlesubmit} >
            <input type="text" 
            name="taskcontent" 
            value={this.state.taskcontent} 
            style={{width:"75%",marginTop: "2%",marginRight: "10%",marginLeft: "10%",marginBottom: "2%",maxLength:"35" }}  
            onChange={this.changehandler}    />
            <br />
             <button type="submit" className="btn btn-success" style={{marginLeft: "40%",marginBottom: "2%"}} >submit</button>
            </form>
           <h1>{this.state.taskcontent}</h1>
           <h1>userid:{this.state.userId}</h1>
           <h1>listId:{this.state.listId}</h1>
           <h1>authToken:{this.state.authToken}</h1>
        </body>
        </html>
           {this.state.alltasks}
            </div>
        )
    }

}
export default View_tasks;
